INSERT INTO hexagrams (id, symbol, name, upper_trigram_id, lower_trigram_id, judgment, symbolism, life_lesson, advice) VALUES
(1, '000000', '乾', 1, 1, '元亨利贞。象征天道运行，刚健有为。', '天、创造、刚健、阳气、领导力、积极进取', '天道酬勤。坚持不懈，发奋图强，方能成就大业。', '此时正是发奋进取的好时机，但需警惕过度自信。'),
(2, '000001', '坤', 2, 2, '元亨，利牝马之贞。象征大地包容，柔顺承载。', '地、承载、柔顺、包容、被动、稳定', '大地无言而包容万物。学会顺应时势，以柔克刚。', '此时宜采取被动、等待的态度，不可急进。'),
(3, '000010', '屯', 5, 3, '元亨利贞，勿用有攸往，利建侯。象征初生困难，需要坚持。', '困难、初生、坚持、突破、创新', '新事物的诞生往往伴随困难。坚持不懈方能冲破难关。', '遇到困难不要退缩，寻求帮助，坚持前行。'),
(4, '000011', '蒙', 7, 5, '亨。匪我求童蒙，童蒙求我，初筮告，再三渎，渎则不告，利贞。象征蒙昧初开，需要教导。', '蒙昧、教育、启蒙、学习、成长', '教育和学习是开启智慧的钥匙。虚心受教，方能成长。', '此时需要寻求指导和教导，虚心学习。'),
(5, '000100', '需', 5, 1, '有孚，光亨，贞吉，利涉大川。象征等待时机，坚持信心。', '等待、耐心、信心、时机、准备', '有时候等待比行动更重要。保持信心，等待时机成熟。', '现在不是行动的时候，需要耐心等待，做好准备。'),
(6, '000101', '讼', 1, 5, '有孚窒惕，中吉，终凶。利见大人，不利涉大川。象征争讼纠纷，需要调和。', '争讼、冲突、纠纷、调和、妥协', '争讼往往导致两败俱伤。寻求调和和妥协是上策。', '避免与人争执，寻求和平解决问题的方式。'),
(7, '000110', '师', 5, 2, '贞，丈人吉。象征军队集结，需要正义的领导。', '军队、纪律、领导、集体、秩序', '集体的力量需要纪律和正义的领导来引导。', '此时需要团结合作，遵守纪律，跟随正义的领导。'),
(8, '000111', '比', 2, 5, '吉。原筮元永贞，无咎。不宁方来，后夫凶。象征亲近团结，需要正确的亲近。', '亲近、团结、合作、友谊、亲密', '亲近和团结需要建立在诚信的基础上。', '此时宜加强与他人的联系和合作。'),
(9, '001000', '小畜', 4, 1, '亨。密云不雨，自我西郊。象征小的积累，需要耐心。', '积累、小成、耐心、积蓄、成长', '小的积累会导致大的成就。要有耐心和恒心。', '此时宜做小的积累和准备，不可急功近利。'),
(10, '001001', '履', 1, 8, '履虎尾，不咥人，亨。象征行走在危险之中，需要谨慎。', '行走、谨慎、危险、勇气、正直', '在危险中行走需要谨慎和勇气。保持正直的品格。', '此时需要谨慎行事，不可轻举妄动。'),
(11, '001010', '泰', 2, 1, '小往大来，吉亨。象征阴阳交泰，天地通达。', '通达、亨通、和谐、交泰、吉祥', '阴阳调和，天地通达。此时是发展的好时机。', '此时运势亨通，可以大展宏图。'),
(12, '001011', '否', 1, 2, '否之匪人，不利君子贞，大往小来。象征阴阳不交，天地闭塞。', '闭塞、否定、困难、衰退、转折', '否极泰来。困难是暂时的，坚持就能看到希望。', '此时运势不佳，需要坚持和等待，不可放弃。'),
(13, '001100', '同人', 6, 1, '同人于野，亨。利涉大川，利君子贞。象征人心相同，众志成城。', '团结、和谐、共识、合作、友谊', '人心相同，其利断金。团结合作能成就大业。', '此时宜加强与他人的沟通和合作。'),
(14, '001101', '大有', 1, 6, '元亨。象征大的拥有和成就，需要谦虚。', '丰富、成就、拥有、富足、吉祥', '大的成就需要谦虚和感恩。不可骄傲自满。', '此时运势很好，但需要保持谦虚的态度。'),
(15, '001110', '谦', 7, 2, '亨。君子有终。象征谦虚谨慎，品德高尚。', '谦虚、谨慎、品德、低调、成功', '谦虚是成功的基石。保持谦虚的态度，才能长久。', '此时宜保持谦虚谨慎的态度，不可骄傲。'),
(16, '001111', '豫', 2, 3, '利建侯行师。象征欢喜和预备，需要及时行动。', '欢喜、预备、准备、及时、行动', '预备充分，才能抓住机会。不可过度沉溺于欢乐。', '此时宜做好准备，抓住机会及时行动。'),
(17, '010000', '随', 8, 3, '元亨利贞，无咎。象征跟随和随顺，需要选择正确的方向。', '跟随、随顺、信任、改变、新的开始', '跟随需要选择正确的方向和领导者。', '此时宜跟随正确的方向，信任他人。'),
(18, '010001', '蛊', 7, 4, '元亨，利涉大川。先甲三日，后甲三日。象征腐蚀和治理，需要改革。', '腐蚀、改革、治理、修复、革新', '腐蚀需要及时治理。改革需要坚定的决心。', '此时需要进行改革和治理，清除腐蚀。'),
(19, '010010', '临', 2, 8, '元亨利贞，至于八月有凶。象征临近和接近，需要把握时机。', '接近、临近、领导、指导、把握', '临近的机会需要把握。领导需要有远见。', '此时机会临近，需要及时把握。'),
(20, '010011', '观', 4, 2, '盥而不荐，有孚颙若。象征观察和被观察，需要谨慎。', '观察、观看、被观察、形象、展示', '观察需要谨慎。被观察需要维护形象。', '此时需要观察形势，谨慎行动。'),
(21, '010100', '噬嗑', 6, 3, '亨。利用狱。象征咬合和处罚，需要公正。', '咬合、处罚、公正、决断、清除', '处罚需要公正。清除障碍需要坚定。', '此时需要做出决断，清除障碍。'),
(22, '010101', '贲', 6, 7, '亨。小利有攸往。象征装饰和文采，需要适度。', '装饰、文采、美化、适度、内在', '装饰需要适度。内在美比外表更重要。', '此时宜注重内在修养，不可过度装饰。'),
(23, '010110', '剥', 2, 7, '不利有攸往。象征剥落和衰退，需要坚守。', '剥落、衰退、失去、坚守、转变', '剥落是必然的过程。坚守本心才能度过难关。', '此时运势衰退，需要坚守本心，等待转机。'),
(24, '010111', '复', 2, 3, '亨。出入无疾，朋来无咎，反复其道，七日来复，利有攸往。象征回复和新生。', '回复、新生、重新开始、反思、改过', '衰退之后必有回复。坚持反思和改过。', '此时是重新开始的好时机，要抓住机会。'),
(25, '011000', '无妄', 1, 3, '元亨利贞。其匪正有眚，不利有攸往。象征无妄和真诚，需要正直。', '真诚、无妄、正直、灾祸、防范', '真诚和正直是处世的基础。防范灾祸需要谨慎。', '此时宜保持真诚和正直，防范意外。'),
(26, '011001', '大畜', 7, 1, '利贞。不家食吉。利涉大川。象征大的积累，需要坚持。', '积累、坚持、蓄积、成长、坚守', '大的成就需要长期的积累和坚持。', '此时宜继续积累和坚持，不可放弃。'),
(27, '011010', '颐', 7, 3, '贞吉。观颐，自求口实。象征养护和自食其力。', '养护、自食其力、修养、健康、自立', '养护自己需要自食其力。修养需要坚持。', '此时宜注重自我修养和健康。'),
(28, '011011', '大过', 8, 4, '栋桡。利有攸往，亨。象征过度和危险，需要谨慎。', '过度、危险、极端、转变、谨慎', '过度往往导致危险。需要适度和平衡。', '此时需要谨慎，避免过度和极端。'),
(29, '011100', '坎', 5, 5, '习坎，有孚，维心亨，行有尚。象征危险和困难，需要坚持。', '危险、困难、坚持、信心、突破', '危险中需要保持信心和坚持。', '此时面临困难，需要坚持和信心。'),
(30, '011101', '离', 6, 6, '利贞，亨。畜牝牛，吉。象征光明和依附，需要正直。', '光明、依附、文明、美丽、聪慧', '光明需要正直来维持。美丽需要内在修养。', '此时宜保持正直和聪慧，发挥才能。'),
(31, '011110', '咸', 8, 7, '亨。利贞。取女吉。象征感应和相互作用，需要真诚。', '感应、相互作用、真诚、爱情、沟通', '感应需要真诚。相互作用需要理解。', '此时宜加强与他人的沟通和感应。'),
(32, '011111', '恒', 3, 4, '亨。无咎。利贞。利有攸往。象征恒久和坚持，需要持之以恒。', '恒久、坚持、持之以恒、稳定、长久', '恒久需要坚持。成功需要长期的努力。', '此时宜坚持不懈，持之以恒。'),
(33, '100000', '遁', 1, 7, '亨。小利贞。象征退避和隐遁，需要适时。', '退避、隐遁、适时、智慧、保护', '适时的退避是智慧。知道何时隐遁是成功的关键。', '此时宜适时退避，保护自己。'),
(34, '100001', '大壮', 3, 1, '利贞。象征力量和壮大，需要正直。', '力量、壮大、勇气、正直、谨慎', '力量需要正直来引导。壮大需要谨慎。', '此时宜发挥力量，但需保持正直。'),
(35, '100010', '晋', 6, 2, '康侯用锡马蕃庶，昼日三接。象征进步和升进，需要谦虚。', '进步、升进、发展、谦虚、光明', '进步需要谦虚。升进需要正直的品格。', '此时运势向上，宜继续努力。'),
(36, '100011', '明夷', 2, 6, '利艰贞。象征光明被伤害，需要坚持。', '伤害、困难、坚持、智慧、隐忍', '光明被伤害时需要坚持。智慧能帮助度过难关。', '此时面临困难，需要坚持和智慧。'),
(37, '100100', '家人', 6, 4, '利女贞。象征家庭和亲情，需要和谐。', '家庭、亲情、和谐、秩序、责任', '家庭需要和谐。亲情需要珍惜。', '此时宜重视家庭和亲情。'),
(38, '100101', '睽', 8, 6, '小事吉。象征分离和对立，需要理解。', '分离、对立、理解、沟通、和解', '分离需要理解。对立需要沟通。', '此时需要加强沟通，化解分歧。'),
(39, '100110', '蹇', 7, 5, '利西南，不利东北。利见大人，贞吉。象征困难和阻碍，需要坚持。', '困难、阻碍、坚持、突破、转折', '困难是暂时的。坚持能突破阻碍。', '此时面临困难，需要坚持和耐心。'),
(40, '100111', '解', 5, 3, '利西南。无所往，其来复吉。有攸往，夙吉。象征解除和释放，需要及时。', '解除、释放、自由、转机、新生', '解除困难需要及时行动。释放需要智慧。', '此时困难即将解除，需要及时把握机会。'),
(41, '101000', '损', 7, 8, '有孚，元吉，无咎，可贞，利有攸往。曷之用，二簋可用享。象征损失和减少，需要适度。', '损失、减少、适度、舍得、改变', '适度的损失能带来更大的收益。舍得是一种智慧。', '此时需要学会舍得，适度减少。'),
(42, '101001', '益', 4, 3, '利有攸往，利涉大川。象征增益和发展，需要行动。', '增益、发展、机会、行动、成长', '增益需要行动。发展需要把握机会。', '此时是发展的好时机，需要及时行动。'),
(43, '101010', '夬', 1, 8, '扬于王庭，孚号有厉，告自邑，不利即戎，利有攸往。象征决断和突破，需要坚定。', '决断、突破、坚定、行动、勇气', '决断需要坚定。突破需要勇气。', '此时需要做出决断，坚定前行。'),
(44, '101011', '姤', 4, 1, '女壮，勿用取女。象征相遇和邂逅，需要谨慎。', '相遇、邂逅、意外、谨慎、防范', '相遇需要谨慎。意外需要防范。', '此时可能有意外相遇，需要谨慎应对。'),
(45, '101100', '萃', 8, 2, '亨。王假之，勿恤，亨。象征聚集和汇聚，需要团结。', '聚集、汇聚、团结、合作、凝聚', '聚集需要团结。汇聚需要共识。', '此时宜加强团结和合作。'),
(46, '101101', '升', 4, 2, '亨。用见大人，勿恤，南征吉。象征上升和进步，需要坚持。', '上升、进步、发展、坚持、成功', '上升需要坚持。进步需要不断努力。', '此时运势上升，宜继续努力。'),
(47, '101110', '困', 8, 5, '亨。贞，大人吉，无咎。言有尚。象征困境和困难，需要坚持。', '困境、困难、坚持、突破、转机', '困境中需要坚持。突破困难需要智慧。', '此时面临困难，需要坚持和耐心。'),
(48, '101111', '井', 5, 4, '改邑不改井，无丧无得，往来井井。象征井水和源头，需要坚守。', '源头、坚守、修养、清洁、滋养', '源头需要坚守。修养需要持续。', '此时宜坚守本心，修养自己。'),
(49, '110000', '革', 6, 8, '己日乃孚，元亨利贞，悔亡。象征革命和变革，需要坚定。', '革命、变革、改变、坚定、新生', '变革需要坚定。改变需要勇气。', '此时是改变的时机，需要坚定前行。'),
(50, '110001', '鼎', 6, 4, '元亨，利贞。象征鼎器和稳定，需要坚守。', '稳定、坚守、成就、权力、传承', '稳定需要坚守。成就需要积累。', '此时宜坚守和稳定，建立基础。'),
(51, '110010', '震', 3, 3, '亨。震来虩虩，笑言哑哑。震惊百里，不丧匕鬯。象征震动和惊恐，需要坚定。', '震动、惊恐、变化、行动、警觉', '震动需要坚定。变化需要适应。', '此时面临变化，需要保持警觉和坚定。'),
(52, '110011', '艮', 7, 7, '艮其背，不获其身，行其庭，不见其人，无咎。象征停止和静止，需要适时。', '停止、静止、适时、坚守、冥想', '停止需要适时。静止需要智慧。', '此时宜停止行动，静观其变。'),
(53, '110100', '渐', 4, 7, '女归吉，利贞。象征渐进和循序渐进，需要耐心。', '渐进、循序渐进、耐心、成长、稳定', '渐进需要耐心。成长需要循序渐进。', '此时宜循序渐进，不可急功近利。'),
(54, '110101', '归妹', 8, 3, '征凶，无攸利。象征归妹和婚姻，需要谨慎。', '婚姻、归妹、承诺、变化、转折', '婚姻需要谨慎。承诺需要负责。', '此时涉及重要承诺，需要谨慎。'),
(55, '110110', '丰', 6, 3, '亨。王假之，勿恤，宜日中。象征丰富和繁荣，需要把握时机。', '丰富、繁荣、把握时机、光明、成就', '丰富需要把握时机。繁荣需要谨慎。', '此时运势丰富，需要把握机会。'),
(56, '110111', '旅', 6, 7, '小亨。旅贞吉。象征旅行和流浪，需要谨慎。', '旅行、流浪、变化、谨慎、适应', '旅行需要谨慎。流浪需要适应。', '此时可能涉及变化和移动，需要谨慎。'),
(57, '111000', '巽', 4, 4, '小亨。利有攸往，利见大人。象征风和柔顺，需要坚持。', '风、柔顺、坚持、渗透、适应', '柔顺需要坚持。渗透需要时间。', '此时宜柔顺和坚持，循序渐进。'),
(58, '111001', '兑', 8, 8, '亨。利贞。象征喜悦和沼泽，需要适度。', '喜悦、沼泽、适度、和谐、交流', '喜悦需要适度。和谐需要交流。', '此时宜保持喜悦和和谐的心态。'),
(59, '111010', '涣', 4, 5, '亨。王假之，无咎。利涉大川。象征涣散和分散，需要凝聚。', '涣散、分散、凝聚、流动、变化', '涣散需要凝聚。分散需要统一。', '此时需要凝聚力量，统一方向。'),
(60, '111011', '节', 5, 8, '亨。苦节不可贞。象征节制和限制，需要适度。', '节制、限制、适度、秩序、自律', '节制需要适度。自律需要坚持。', '此时宜节制和自律，保持秩序。'),
(61, '111100', '中孚', 4, 8, '豚鱼吉。利涉大川，利贞。象征中心和信任，需要真诚。', '信任、真诚、中心、沟通、和谐', '信任需要真诚。沟通需要理解。', '此时宜加强信任和沟通。'),
(62, '111101', '小过', 7, 3, '亨。利贞。可小事，不可大事。飞鸟遗之音，不宜上，宜下，大吉。象征小的超越，需要谨慎。', '小过、谨慎、超越、变化、警觉', '小过需要谨慎。超越需要适度。', '此时需要谨慎，避免过度。'),
(63, '111110', '既济', 6, 5, '亨小，利贞。初吉终乱。象征完成和成功，需要谨慎。', '完成、成功、谨慎、转折、新的开始', '完成需要谨慎。成功需要维持。', '此时已接近成功，但需要谨慎维持。'),
(64, '111111', '未济', 5, 6, '亨。小狐汔济，濡其尾，无攸利。象征未完成和新的开始，需要坚持。', '未完成、新的开始、坚持、希望、转机', '未完成需要坚持。新的开始需要希望。', '此时是新的开始，需要坚持和希望。');